export const template = "import * as React from 'react';\nimport { Image } from 'react-native';\nimport {\n  ColorMatrix,\n  concatColorMatrices,\n  __IMPORTS__\n} from 'react-native-color-matrix-image-filters';\n\nexport default class FilteredImage extends React.Component {\n  frameId = undefined;\n  state = {};\n\n  constructor(props) {\n    super(props);\n\n    this.state = {\n      __INITSTATE__\n    };\n  }\n\n  componentDidMount() {\n    this.animate();\n  }\n\n  componentWillUnmount() {\n    cancelAnimationFrame(this.frameId);\n  }\n\n  animate = () => {\n    this.setState((state, props) => {\n      __ANIMATE__\n    });\n    this.frameId = requestAnimationFrame(this.animate);\n  }\n\n  render() {\n    const {\n      __PROPS__\n      ...imageProps\n    } = this.props;\n\n    const {\n      __STATE__\n    } = this.state;\n\n    const matrix = concatColorMatrices([\n      __MATRICES__\n    ]);\n\n    return (\n      <ColorMatrix matrix={matrix}>\n        <Image {...imageProps} />\n      </ColorMatrix>\n    );\n  }\n}\n";